mapper.select:

select 

from ORD_ORDER_DETAIL

  
    and ID = #{id,jdbcType=BIGINT}
  
  
    and ORDER_ID = #{orderId,jdbcType=BIGINT}
  
  
    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}
  
  
    and PRODUCE_ID = #{produceId,jdbcType=BIGINT}
  
  
    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}
  
  
    and SPEC_NAME = #{specName,jdbcType=VARCHAR}
  
  
    and BUY_COUNT = #{buyCount,jdbcType=INTEGER}
  
  
    and BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}
  
  
    and CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}
  
  
    and RETURN_COUNT = #{returnCount,jdbcType=INTEGER}
  
  
    and CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}
  
  
    and BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}
  
  
    and RETURN_STATE = #{returnState,jdbcType=TINYINT}


select count(*) 
from ORD_ORDER_DETAIL
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ORD_ORDER_DETAIL

  
    and ID = #{id,jdbcType=BIGINT}
  
  
    and ORDER_ID = #{orderId,jdbcType=BIGINT}
  
  
    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}
  
  
    and PRODUCE_ID = #{produceId,jdbcType=BIGINT}
  
  
    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}
  
  
    and SPEC_NAME = #{specName,jdbcType=VARCHAR}
  
  
    and BUY_COUNT = #{buyCount,jdbcType=INTEGER}
  
  
    and BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}
  
  
    and CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}
  
  
    and RETURN_COUNT = #{returnCount,jdbcType=INTEGER}
  
  
    and CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}
  
  
    and BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}
  
  
    and RETURN_STATE = #{returnState,jdbcType=TINYINT}

mapper.update:
	UPDATE ORD_ORDER_DETAIL SET RETURN_COUNT=#{returnCount,jdbcType=INTEGER}, CASHBACK_TOTAL=#{cashbackTotal,jdbcType=DECIMAL} 
	
    ,RETURN_STATE = #{returnState,jdbcType=TINYINT}
  
	WHERE ID=#{id,jdbcType=BIGINT} AND ORDER_ID=#{orderId,jdbcType=BIGINT}
